import Axios from "axios";
import { useState } from "react";
function App() {
  const [tasklist, settasklist] = useState([]);
  const [title, settitle] = useState("");
  const [detail, setdetail] = useState("");
  const [date, setdate] = useState("");
  const [time, settime] = useState("");

  const gettask = () => {
    Axios.get("http://localhost:3001/todolist").then((response) => {
      settasklist(response.data);
    });
  };

  const addTask = () => {
    Axios.post("http://localhost:3001/create", {
      title: title,
      detail: detail,
      date: date,
      time: time
    }).then(() => {
      setTaskList([...tasklist, {
        title: title,
        detail: detail,
        date: date,
        time: time
      }]);
    });
  };


  return (
    <div className="App container">
      <h1>TODO list</h1>
      <div className="taskDetail">
        <form action="">
          <div className="mb-3">
            <label htmlFor="taskTitle" className="form-label">
              Task Title:
            </label>
            <input
              type="text"
              className="form-control"
              id="taskTitle"
              placeholder="Enter task"
              onChange={(event) => {
                settitle(event.target.value);
              }}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="taskDetail" className="form-label">
              Task detail:
            </label>
            <input
              type="text"
              className="form-control"
              id="taskTitle"
              placeholder="Enter detail"
              onChange={(event) => {
                setdetail(event.target.value);
              }}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="taskDate" className="form-label">
              Task date-time:
            </label>
            <input
              type="date"
              className="form-control"
              id="taskTitle"
              placeholder="Enter date"
              onChange={(event) => {
                setdate(event.target.value);
              }}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="taskTime" className="form-label">
              Task date-time:
            </label>
            <input
              type="time"
              className="form-control"
              id="taskTitle"
              placeholder="Enter time"
              onChange={(event) => {
                settime(event.target.value);
              }}
            />
          </div>
          <button className="btn btn-success">Add Task</button>
        </form>
      </div>
      <hr />
      <div className="todoList">
        <button className="btn btn-primary mb-3" onClick={gettask}>
          Show Task
        </button>
        {tasklist.map((val, key) => {
          return (
            <div className="task card mb-3">
              <div className="card-body text-left">
                <p className="card-text">Titile: {val.title}</p>
                <p className="card-text">Detail: {val.detail}</p>
                <p className="card-text">Date: {val.date}</p>
                <p className="card-text">Time: {val.time}</p>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default App;
