function TaskDetail(props) {
    const {val,onValueChange} = props
  return ( 
    <div className="taskDetail">
      <form action="">
        <div className="mb-3">
          <label htmlFor="taskTitle" className="form-label">
            Task Title:
          </label>
          <input
            type="text"
            className="form-control"
            id="taskTitle"
            placeholder="Enter task"
            value={val}
            onChange={(event) => {
              onValueChange(event.target.val);
            }}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="taskDetail" className="form-label">
            Task Detail:
          </label>
          <input
            type="text"
            className="form-control"
            id="taskTitle"
            placeholder="Enter detail"
            value={val}
            onChange={(event) => {
                onValueChange(event.target.val);
            }}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="taskDate" className="form-label">
            Task Date:
          </label>
          <input
            type="date"
            className="form-control"
            id="taskTitle"
            placeholder="Enter date"
            onChange={(event) => {
                onValueChange(event.target.value);
            }}
          />
        </div>
        <div className="mb-3">
          <label htmlFor="taskTime" className="form-label">
            Task Time:
          </label>
          <input
            type="time"
            className="form-control"
            id="taskTitle"
            placeholder="Enter time"
            onChange={(event) => {
                onValueChange(event.target.value);
            }}
          />
        </div>
      </form>
    </div>
  );
}

export default TaskDetail;
